import React, { useState } from 'react';
import { AnimatePresence, motion } from 'framer-motion';
import dishesData from './dishesData';
import axios from 'axios';

const Menu = ({ addToCart, cart, tableNumber }) => {
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('All');

  const categories = ['All', ...new Set(dishesData.map((dish) => dish.category))];

  const filteredDishes = dishesData.filter(
    (dish) =>
      dish.name.toLowerCase().includes(searchTerm.toLowerCase()) &&
      (selectedCategory === 'All' || dish.category === selectedCategory)
  );

  const handlePayment = async () => {
    try {
      const orderResponse = await axios.post(
        `${process.env.REACT_APP_API_URL}/orders`,
        {
          tableNumber,
          items: cart,
          total: cart.reduce((acc, item) => acc + item.price, 0),
        }
      );

      const paymentResponse = await axios.post(
        `${process.env.REACT_APP_API_URL}/orders/pay`,
        {
          orderId: orderResponse.data._id,
          amount: orderResponse.data.total,
        }
      );

      window.location.href = paymentResponse.data.redirect_url;
    } catch (error) {
      console.error('Payment failed', error);
    }
  };

  return (
    <div className="menu">
      <div className="search-filter">
        <input
          type="text"
          placeholder="Search dishes..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
        />
        <div className="categories">
          {categories.map((category) => (
            <button
              key={category}
              onClick={() => setSelectedCategory(category)}
              className={
                selectedCategory === category ? 'active-category' : 'category-button'
              }
            >
              {category}
            </button>
          ))}
        </div>
      </div>
      <div className="dishes">
        {filteredDishes.map((dish) => (
          <div key={dish.id} className="dish-card">
            <img src={dish.image} alt={dish.name} />
            <h3>{dish.name}</h3>
            <p>{dish.description}</p>
            <div className="dish-footer">
              <span>£{dish.price.toFixed(2)}</span>
              <button onClick={() => addToCart(dish)}>Add</button>
            </div>
          </div>
        ))}
      </div>
      {cart.length > 0 && (
        <div className="cart-footer">
          <button onClick={handlePayment}>
            Pay Now (£{cart.reduce((acc, item) => acc + item.price, 0).toFixed(2)})
          </button>
        </div>
      )}
    </div>
  );
};

export default Menu;
